  if(loading) return <h3>Loading......</h3>;

  if (error) return <h3>We know something went wrong, we are taking care of that.</h3>
  console.log (data);
 
  // return(
  //   <>
  //   <h1>hi{data.title}</h1>
  //   <Card
  //   //  image={data.image}
  //   //  title={data.title}
  //    price={data.price}   
    
  //   />
  //   </>
    
  // )
  
}



let fetchdata= fetch(
  "https://fakestoreapi.com/products/")
    
          // fetchRes is the promise to resolve
          // it by using.then() method
          .then(res =>
              res.json()).then(d =>{
               console.log(d);
              d.map((original)=>{

                return (
                  <Card
                      image={original.image}
                      title={original.title}
                      price={original.price}   
                      
                      />
                )
              })
              
              
              }).catch((err)=>{
                 console.log(err);


                })  


function Main(){

  
    return (
      <div className="bdy">
      <div className='banner'>
        <img src={Image}></img>  
        </div>
         <fetchdata/>
      </div>
    
  
    )
  }

  export default Main;
