import Image from "../Banner.jfif";
import Card from "./Card";
import Useapi from "./Useapi";


function locate(){

  const{data, error, loading} = Useapi('https://fakestoreapi.com/products');
  if(loading) return <h3>Loading......</h3>;

  if (error) return <h3>We know something went wrong, we are taking care of that.</h3>
  console.log (data[1].title);
  return(
    <Card
     image={data[1].image}
     title={data[1].title}
     price={data[1].price}   
    
    />
  )
  
}


function Main(){

  
    return (
      <div className="bdy">
      <div className='banner'>
        <img src={Image}></img>  
        </div>
        {locate}
      </div>
    
  
    )
  }

  export default Main;
